import React, { Component } from "react";
import styled from "styled-components";
import Delay from "react-delay-render";
import Logo from "../../common/Logo";
import AnchorLink from "react-anchor-link-smooth-scroll";

const NavContent = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;

  width: 100%;
  height: 100%;
  max-height: 60px;

  // remove this and put on individual items
  z-index: 5;

  display: flex;
  justify-content: center;

  padding-top: 1rem;
`;

const NavLinks = styled.div`
  position: relative;

  display: flex;
  justify-content: flex-end;
  align-items: center;

  height: 100%;

  margin-left: 1rem;
  margin-right: 1rem;

  @media only screen and (max-width: 640px) {
    flex-direction: column;
  }
`;

const NavLink = styled(AnchorLink)`
  color: #b0b2c3;

  padding-bottom: 3px;
  padding-top: 3px;

  font-weight: bold;
  font-size: 1.2rem;

  margin-left: 2.5rem;

  transition: all 0.5s ease;

  position: relative;

  display: inline-block;

  overflow: hidden;

  &:after {
    content: " ";
    width: 100%;
    height: 3px;
    background: #f25757;
    position: absolute;
    left: 0;
    bottom: 0;
    animation: Underline3 0.3s forwards;
  }
  &:before {
    content: " ";
    width: 100%;
    height: 3px;
    background: #235aa6;
    position: absolute;
    left: 0;
    bottom: 0;
    animation: Underline4 0.6s forwards;
  }

  &:hover {
    color: #fff;

    &:hover:after {
      content: " ";
      width: 100%;
      height: 3px;
      background: #f25757;
      position: absolute;
      left: 0;
      bottom: 0;
      animation: Underline 0.3s forwards;
    }

    &:hover:before {
      content: " ";
      width: 100%;
      height: 3px;
      background: #235aa6;
      position: absolute;
      left: 0;
      bottom: 0;
      animation: Underline2 0.3s forwards;
    }
  }
`;

const NavLink2 = styled.a`
  color: #b0b2c3;

  padding-bottom: 3px;
  padding-top: 3px;

  font-weight: bold;
  font-size: 1.2rem;

  margin-left: 2.5rem;

  transition: all 0.5s ease;

  position: relative;

  display: inline-block;

  overflow: hidden;

  &:after {
    content: " ";
    width: 100%;
    height: 3px;
    background: #f25757;
    position: absolute;
    left: 0;
    bottom: 0;
    animation: Underline3 0.3s forwards;
  }
  &:before {
    content: " ";
    width: 100%;
    height: 3px;
    background: #235aa6;
    position: absolute;
    left: 0;
    bottom: 0;
    animation: Underline4 0.6s forwards;
  }

  &:hover {
    color: #fff;

    &:hover:after {
      content: " ";
      width: 100%;
      height: 3px;
      background: #f25757;
      position: absolute;
      left: 0;
      bottom: 0;
      animation: Underline 0.3s forwards;
    }

    &:hover:before {
      content: " ";
      width: 100%;
      height: 3px;
      background: #235aa6;
      position: absolute;
      left: 0;
      bottom: 0;
      animation: Underline2 0.3s forwards;
    }
  }
`;

const TopLogo = styled.div`
  @media only screen and (max-width: 640px) {
    display: none;

    border: 5px solid red;
  }
`;

class Header extends Component {
  render() {
    return (
      <NavContent>
        <div className="header_content main_content">
          <TopLogo>
            <Logo />
          </TopLogo>
          <NavLinks>
            <NavLink className="NavLink" href="#projects">
              <span style={{ zIndex: "2", position: "relative" }}>
                Projects
              </span>
            </NavLink>
            <NavLink className="NavLink2" href="mailto:logantanous@gmail.com">
              Contact
            </NavLink>
          </NavLinks>
        </div>
      </NavContent>
    );
  }
}

export default Delay({ delay: 1500 })(Header);
